import React, { useState } from 'react';
import { useStore } from '../../store';
import { Plus, Check, Heart, MessageCircle, Minus } from 'lucide-react';
import { ProductSuggestion } from '../../components/ProductSuggestion';
import { Product } from '../../types';

export const ShoppingList: React.FC = () => {
  const { 
    shoppingItems, 
    addShoppingItem, 
    updateItemQuantity,
    toggleItemCheck, 
    toggleItemLike,
    currentUser,
    currentFamily,
    mockUsers 
  } = useStore();

  const [showAddItem, setShowAddItem] = useState(false);
  const [itemName, setItemName] = useState('');
  const [itemComment, setItemComment] = useState('');
  const [selectedProduct, setSelectedProduct] = useState<Product | null>(null);

  const handleAddItem = (e: React.FormEvent) => {
    e.preventDefault();
    if (itemName.trim()) {
      addShoppingItem(
        itemName, 
        itemComment || undefined, 
        selectedProduct?.image, 
        1
      );
      setItemName('');
      setItemComment('');
      setSelectedProduct(null);
      setShowAddItem(false);
    }
  };

  const handleProductSelect = (product: Product) => {
    setSelectedProduct(product);
    // You can also auto-fill the comment with product description if desired
    // setItemComment(product.description);
  };

  const uncheckedItems = shoppingItems.filter(item => !item.checked);
  const checkedItems = shoppingItems.filter(item => item.checked);

  return (
    <div className="shopping-list-screen">
      <div className="list-header">
        <h2>{currentFamily?.name}'s List</h2>
        <button
          className="btn-add"
          onClick={() => setShowAddItem(!showAddItem)}
        >
          <Plus size={24} />
        </button>
      </div>

      {showAddItem && (
        <form onSubmit={handleAddItem} className="add-item-form">
          <ProductSuggestion
            value={itemName}
            onChange={setItemName}
            onProductSelect={handleProductSelect}
            placeholder="Search for products or enter custom item..."
            className="product-suggestion-input"
          />
          {selectedProduct && (
            <div className="selected-product-info">
              <span className="selected-product-text">Selected: {selectedProduct.name}</span>
              {selectedProduct.brand && <span className="selected-product-brand">({selectedProduct.brand})</span>}
            </div>
          )}
          <input
            type="text"
            placeholder="Comment (optional)"
            value={itemComment}
            onChange={(e) => setItemComment(e.target.value)}
            className="input"
          />
          <div className="form-actions">
            <button type="button" onClick={() => {
              setShowAddItem(false);
              setItemName('');
              setItemComment('');
              setSelectedProduct(null);
            }} className="btn btn-secondary">
              Cancel
            </button>
            <button type="submit" className="btn btn-primary">
              Add Item
            </button>
          </div>
        </form>
      )}

      <div className="items-list">
        {uncheckedItems.length === 0 && checkedItems.length === 0 && (
          <div className="empty-state">
            <p>No items yet. Add your first item!</p>
          </div>
        )}

        {uncheckedItems.map((item) => (
          <div key={item.id} className="shopping-item">
            <button
              className="check-btn"
              onClick={() => toggleItemCheck(item.id)}
            >
              <div className="checkbox" />
            </button>

            {item.image && (
              <div className="item-image">
                <img 
                  src={item.image} 
                  alt={item.name}
                  onError={(e) => {
                    e.currentTarget.style.display = 'none';
                  }}
                />
              </div>
            )}

            <div className="item-content">
              <div className="item-header">
                <h3>{item.name}</h3>
                <div className="item-meta">
                  <span className="added-by">
                    <span className="avatar-mini">{item.addedByAvatar}</span>
                    {item.addedByName}
                  </span>
                </div>
              </div>
              {item.comment && (
                <p className="item-comment">
                  <MessageCircle size={14} />
                  {item.comment}
                </p>
              )}
            </div>

            <div className="item-actions">
              <div className="quantity-controls">
                <button
                  className="quantity-btn"
                  onClick={() => updateItemQuantity(item.id, item.quantity - 1)}
                  disabled={item.quantity <= 1}
                >
                  <Minus size={16} />
                </button>
                <span className="quantity">{item.quantity}</span>
                <button
                  className="quantity-btn"
                  onClick={() => updateItemQuantity(item.id, item.quantity + 1)}
                >
                  <Plus size={16} />
                </button>
              </div>

              <button
                className={`like-btn ${item.likedBy.includes(currentUser?.id || '') ? 'liked' : ''}`}
                onClick={() => toggleItemLike(item.id)}
              >
                <Heart size={20} />
                {item.likedBy.length > 0 && (
                  <span className="like-count">{item.likedBy.length}</span>
                )}
              </button>
            </div>
          </div>
        ))}

        {checkedItems.length > 0 && (
          <>
            <div className="section-divider">
              <span>Completed</span>
            </div>
            {checkedItems.map((item) => (
              <div key={item.id} className="shopping-item checked">
                <button
                  className="check-btn"
                  onClick={() => toggleItemCheck(item.id)}
                >
                  <div className="checkbox checked">
                    <Check size={16} />
                  </div>
                </button>

                {item.image && (
                  <div className="item-image">
                    <img 
                      src={item.image} 
                      alt={item.name}
                      onError={(e) => {
                        e.currentTarget.style.display = 'none';
                      }}
                    />
                  </div>
                )}

                <div className="item-content">
                  <div className="item-header">
                    <h3>{item.name} {item.quantity > 1 && <span className="quantity-badge">Ã—{item.quantity}</span>}</h3>
                    <div className="item-meta">
                      <span className="added-by">
                        <span className="avatar-mini">{item.addedByAvatar}</span>
                        {item.addedByName}
                      </span>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </>
        )}
      </div>
    </div>
  );
};
