import React, { useState, useEffect, useRef } from 'react';
import { Product } from '../types';
import { ChevronDown, Search } from 'lucide-react';
import { CSVDataLoader } from '../utils/csvLoader';

interface ProductSuggestionProps {
  value: string;
  onChange: (value: string) => void;
  onProductSelect?: (product: Product) => void;
  placeholder?: string;
  className?: string;
}

const MOCK_PRODUCTS: Product[] = [
  { id: '1', name: 'English Cucumber', description: 'Fresh English cucumber, 1 ea', image: 'https://assets.shop.loblaws.ca/products/20070132001/b2/en/front/20070132001_front_a06_@2.png' },
  { id: '2', name: 'Green Onion', description: 'Fresh green onion bunch', image: 'https://assets.shop.loblaws.ca/products/20107500001/b2/en/front/20107500001_front_a06_@2.png' },
  { id: '3', name: 'Roma Tomatoes', description: 'Fresh Roma tomatoes, sold by weight', image: 'https://assets.shop.loblaws.ca/products/20143381001/b2/en/front/20143381001_front_a06_@2.png' },
  { id: '4', name: 'Broccoli', description: 'Fresh broccoli crown, 1 ea', image: 'https://assets.shop.loblaws.ca/products/20145621001/b2/en/front/20145621001_front_a06_@2.png' },
  { id: '5', name: 'Red Peppers', description: 'Fresh red bell peppers, sold by weight', image: 'https://assets.shop.loblaws.ca/products/20007535001/b2/en/front/20007535001_front_a06_@2.png' },
  { id: '6', name: 'Yellow Onions', description: 'Yellow onions, 3 lb bag', image: 'https://assets.shop.loblaws.ca/products/20811994001/b2/en/front/20811994001_front_a06_@2.png' },
  { id: '7', name: 'Celery Stalks', description: 'Fresh celery stalks, 1 ea', image: 'https://assets.shop.loblaws.ca/products/20163119001/b2/en/front/20163119001_front_a06_@2.png' },
  { id: '8', name: 'Lettuce Iceberg', description: 'Fresh iceberg lettuce head, 1 ea', image: 'https://assets.shop.loblaws.ca/products/20135326001/b2/en/front/20135326001_front_a06_@2.png' },
  { id: '9', name: 'Red Onion', description: 'Fresh red onions, sold by weight', image: 'https://assets.shop.loblaws.ca/products/20134101001/b2/en/front/20134101001_front_a06_@2.png' },
  { id: '10', name: 'Cauliflower', description: 'Fresh cauliflower head, 1 ea', image: 'https://assets.shop.loblaws.ca/products/20135377001/b2/en/front/20135377001_front_a06_@2.png' },
  { id: '11', name: 'Sweet Green Peppers', description: 'Fresh green bell peppers, sold by weight', image: 'https://assets.shop.loblaws.ca/products/20425893001/b2/en/front/20425893001_front_a06_@2.png' },
  { id: '12', name: 'Zucchini', description: 'Fresh zucchini, sold by weight', image: 'https://assets.shop.loblaws.ca/products/20426596001/b2/en/front/20426596001_front_a06_@2.png' },
  { id: '13', name: 'Garlic Bulbs', description: 'Fresh garlic bulbs, 3-count pack', image: 'https://assets.shop.loblaws.ca/products/21004355001/b2/en/front/21004355001_front_a06_@2.png' },
  { id: '14', name: 'Ginger', description: 'Fresh ginger root, sold by weight', image: 'https://assets.shop.loblaws.ca/products/20179038001/b2/en/front/20179038001_front_a06_@2.png' },
  { id: '15', name: 'Mini Cucumbers', description: 'Mini cucumbers, 15-pack', image: 'https://assets.shop.loblaws.ca/products/21035960001/b2/en/front/21035960001_front_a06_@2.png' },
  { id: '16', name: 'White Potatoes', description: 'White potatoes, 10 lb bag', image: 'https://assets.shop.loblaws.ca/products/20600997001/b2/en/front/20600997001_front_a06_@2.png' },
  { id: '17', name: 'Sweet Potato', description: 'Fresh sweet potatoes, sold by weight', image: 'https://assets.shop.loblaws.ca/products/20127708001/b2/en/front/20127708001_front_a06_@2.png' },
  { id: '18', name: 'Grape Tomatoes', description: 'Fresh grape tomatoes, 283g container', image: 'https://assets.shop.loblaws.ca/products/21367888001/b2/en/front/21367888001_front_a06_@2.png' },
  { id: '19', name: 'Sweet Onion', description: 'Fresh sweet onions, sold by weight', image: 'https://assets.shop.loblaws.ca/products/20083454001/b2/en/front/20083454001_front_a06_@2.png' },
  { id: '20', name: 'Romaine Lettuce', description: 'Fresh romaine lettuce head, 1 ea', image: 'https://assets.shop.loblaws.ca/products/20065036001/b2/en/front/20065036001_front_a06_@2.png' },
  // Add more common grocery items
  { id: '21', name: 'Bananas', description: 'Fresh bananas, sold by weight', brand: 'No Name' },
  { id: '22', name: 'Milk 2%', description: 'Partly skimmed milk, 4L jug', brand: 'No Name' },
  { id: '23', name: 'Bread White', description: 'White sandwich bread, 675g loaf', brand: 'No Name' },
  { id: '24', name: 'Eggs Large', description: 'Large white eggs, 12-count', brand: 'No Name' },
  { id: '25', name: 'Chicken Breast', description: 'Boneless skinless chicken breast', brand: 'No Name' },
  { id: '26', name: 'Ground Beef', description: 'Lean ground beef, sold by weight', brand: 'No Name' },
  { id: '27', name: 'Pasta Spaghetti', description: 'Spaghetti pasta, 900g box', brand: 'No Name' },
  { id: '28', name: 'Rice White', description: 'Long grain white rice, 2kg bag', brand: 'No Name' },
  { id: '29', name: 'Yogurt Greek', description: 'Plain Greek yogurt, 750g container', brand: 'No Name' },
  { id: '30', name: 'Cheese Cheddar', description: 'Medium cheddar cheese, 400g block', brand: 'No Name' },
];

export const ProductSuggestion: React.FC<ProductSuggestionProps> = ({
  value,
  onChange,
  onProductSelect,
  placeholder = "Enter product name...",
  className = ""
}) => {
  const [isOpen, setIsOpen] = useState(false);
  const [filteredProducts, setFilteredProducts] = useState<Product[]>([]);
  const [highlightedIndex, setHighlightedIndex] = useState(-1);
  const [isLoadingData, setIsLoadingData] = useState(false);
  const dropdownRef = useRef<HTMLDivElement>(null);
  const inputRef = useRef<HTMLInputElement>(null);

  // Load CSV data on component mount
  useEffect(() => {
    const loadData = async () => {
      setIsLoadingData(true);
      try {
        await CSVDataLoader.loadProducts();
      } catch (error) {
        console.error('Failed to load product data:', error);
      } finally {
        setIsLoadingData(false);
      }
    };

    loadData();
  }, []);

  useEffect(() => {
    if (value.length >= 2 && !isLoadingData) {
      const filtered = CSVDataLoader.searchProducts(value, 8);
      setFilteredProducts(filtered);
      setIsOpen(filtered.length > 0);
    } else {
      setFilteredProducts([]);
      setIsOpen(false);
    }
    setHighlightedIndex(-1);
  }, [value, isLoadingData]);

  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target as Node)) {
        setIsOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (!isOpen) return;

    switch (e.key) {
      case 'ArrowDown':
        e.preventDefault();
        setHighlightedIndex(prev => 
          prev < filteredProducts.length - 1 ? prev + 1 : prev
        );
        break;
      case 'ArrowUp':
        e.preventDefault();
        setHighlightedIndex(prev => prev > 0 ? prev - 1 : -1);
        break;
      case 'Enter':
        e.preventDefault();
        if (highlightedIndex >= 0) {
          handleProductSelect(filteredProducts[highlightedIndex]);
        }
        break;
      case 'Escape':
        setIsOpen(false);
        setHighlightedIndex(-1);
        break;
    }
  };

  const handleProductSelect = (product: Product) => {
    onChange(product.name);
    onProductSelect?.(product);
    setIsOpen(false);
    setHighlightedIndex(-1);
  };

  return (
    <div className={`product-suggestion ${className}`} ref={dropdownRef}>
      <div className="input-container">
        <input
          ref={inputRef}
          type="text"
          value={value}
          onChange={(e) => onChange(e.target.value)}
          onKeyDown={handleKeyDown}
          placeholder={placeholder}
          className="input product-input"
          autoComplete="off"
        />
        <Search className="search-icon" size={16} />
      </div>

      {isOpen && filteredProducts.length > 0 && (
        <div className="suggestions-dropdown">
          {filteredProducts.map((product, index) => (
            <div
              key={product.id}
              className={`suggestion-item ${index === highlightedIndex ? 'highlighted' : ''}`}
              onClick={() => handleProductSelect(product)}
              onMouseEnter={() => setHighlightedIndex(index)}
            >
              {product.image && (
                <img 
                  src={product.image} 
                  alt={product.name}
                  className="product-image"
                  onError={(e) => {
                    e.currentTarget.style.display = 'none';
                  }}
                />
              )}
              <div className="product-info">
                <div className="product-name">{product.name}</div>
                <div className="product-description">{product.description}</div>
                {product.brand && (
                  <div className="product-brand">{product.brand}</div>
                )}
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};
